import random
secret_number = random.randint(1, 10)
print('I\'m thinking of a number between 1 and 10. You have 3 guesses!')

for attempt in range(3):
    guess = int(input(f'Attempt {attempt + 1}: Enter your guess: '))
    if guess == secret_number:
        print('Congratulations! You guessed it!')
        break
    elif guess < secret_number:
        print('Too low!')
    else:
        print('Too high!')
else:
    print(f'Sorry, the number was {secret_number}')

even_count = 0
odd_count = 0

print('Enter numbers (enter 0 to stop):')
while True:
    number = int(input('Enter a number: '))
    if number == 0:
        break
    elif number % 2 == 0:
        even_count += 1
    else:
        odd_count += 1

print(f'Even numbers entered: {even_count}')
print(f'Odd numbers entered: {odd_count}')

total_score = 0
print('Enter 5 test scores:')

for i in range(5):
    score = float(input(f'Enter score {i + 1}: '))
    total_score += score

average = total_score / 5
print(f'Average score: {average:.1f}')

if average >= 90:
    grade = 'A'
elif average >= 80:
    grade = 'B'
elif average >= 70:
    grade = 'C'
elif average >= 60:
    grade = 'D'
else:
    grade = 'F'

print(f'Letter grade: {grade}')
